package xwing

import (
	"bytes"
	"encoding/hex"
	"testing"
)

func TestRoundTrip(t *testing.T) {
	ek, dk, err := GenerateKey()
	if err != nil {
		t.Fatal(err)
	}
	c, Ke, err := Encapsulate(ek)
	if err != nil {
		t.Fatal(err)
	}
	Kd, err := Decapsulate(dk, c)
	if err != nil {
		t.Fatal(err)
	}
	if !bytes.Equal(Ke, Kd) {
		t.Errorf("Ke != Kd")
	}

	ek1, dk1, err := GenerateKey()
	if err != nil {
		t.Fatal(err)
	}
	if bytes.Equal(ek, ek1) {
		t.Errorf("ek == ek1")
	}
	if bytes.Equal(dk, dk1) {
		t.Errorf("dk == dk1")
	}

	c1, Ke1, err := Encapsulate(ek)
	if err != nil {
		t.Fatal(err)
	}
	if bytes.Equal(c, c1) {
		t.Errorf("c == c1")
	}
	if bytes.Equal(Ke, Ke1) {
		t.Errorf("Ke == Ke1")
	}
}

var sink byte

func BenchmarkKeyGen(b *testing.B) {
	b.ResetTimer()
	for i := 0; i < b.N; i++ {
		ek, dk, err := GenerateKey()
		if err != nil {
			b.Fatal(err)
		}
		sink ^= ek[0] ^ dk[0]
	}
}

func BenchmarkEncaps(b *testing.B) {
	ek, _, err := GenerateKey()
	if err != nil {
		b.Fatal(err)
	}
	b.ResetTimer()
	for i := 0; i < b.N; i++ {
		c, K, err := Encapsulate(ek)
		if err != nil {
			b.Fatal(err)
		}
		sink ^= c[0] ^ K[0]
	}
}

func BenchmarkDecaps(b *testing.B) {
	ek, dk, err := GenerateKey()
	if err != nil {
		b.Fatal(err)
	}
	c, _, err := Encapsulate(ek)
	if err != nil {
		b.Fatal(err)
	}
	b.ResetTimer()
	for i := 0; i < b.N; i++ {
		K, err := Decapsulate(dk, c)
		if err != nil {
			b.Fatal(err)
		}
		sink ^= K[0]
	}
}

func TestVector(t *testing.T) {
	// https://www.ietf.org/archive/id/draft-connolly-cfrg-xwing-kem-01.html#appendix-A
	seed, _ := hex.DecodeString("7f9c2ba4e88f827d616045507605853ed73b8093f6efbc88eb1a6eacfa66ef263cb1eea988004b93103cfb0aeefd2a686e01fa4a58e8a3639ca8a1e3f9ae57e235b8cc873c23dc62b8d260169afa2f75ab916a58d974918835d25e6a435085b2")
	skExp, _ := hex.DecodeString("")
	pkExp, _ := hex.DecodeString("")
	ct, _ := hex.DecodeString("")
	ssExp, _ := hex.DecodeString("2fae7214767890c4703fad953f5e3f91303111498caa135d77cde634151e71b5")

	pk, sk, err := NewKeyFromSeed(seed)
	if err != nil {
		t.Fatal(err)
	}
	if !bytes.Equal(pk, pkExp) {
		t.Errorf("pk != pkExp")
	}
	if !bytes.Equal(sk, skExp) {
		t.Errorf("sk != skExp")
	}

	if _, _, err := Encapsulate(pk); err != nil {
		t.Fatal(err)
	}

	ss, err := Decapsulate(sk, ct)
	if err != nil {
		t.Fatal(err)
	}
	if !bytes.Equal(ss, ssExp) {
		t.Errorf("ss != ssExp")
	}
}
